import React from 'react'
import { useContext, useEffect, useState } from 'react'
import { StoreContext } from '../../context/StoreContext'
import axios from 'axios'
import './MyOrders.css'
import { assets } from '../../assets/assets'

const MyOrders = () => {
    const {url, token} = useContext(StoreContext);
    const [data, setData] = useState([]);

    // Fetch orders from the backend
    //const fetchOrders=async () => {
      //  const response = await axios.post(url+'/api/order/userorders', {},{headers:{token}});
      //  setData(response.data.orders);
    //    console.log(response.data.orders);
//}


useEffect(() => {
  const fetchOrders = async () => {
    try {
      const response = await axios.post(
        url + '/api/order/userorders',
        {},
        { headers: { token } }
      );
      if (response.data.orders) {
        setData(response.data.orders);
      } else {
        setData([]);
      }
    } catch (err) {
      console.error("Failed to fetch orders:", err);
      setData([]);
    }
  };

  if (token) {
    fetchOrders();
  }
}, [token, url]); // âœ… now depends only on token and url

return (
  <div className='my-orders'>
    <h2>My Orders</h2>
    <div className="container">
      {data.map((order, index) => (
        <div key={index} className="my-orders-order">
          {/* âœ… Show first itemâ€™s image if available, else parcel icon */}
          <img 
            src={order.items[0]?.image || assets.parcel_icon} 
            alt="Order Preview" 
          />

          {/* âœ… Order details */}
          <p>
            {order.items.map((item, i) =>
              i === order.items.length - 1
                ? `${item.name} x ${item.quantity}`
                : `${item.name} x ${item.quantity}, `
            )}
          </p>

          <p>ðŸ’° ${order.amount}.00</p>
          <p>ðŸ“¦ Items: {order.items.length}</p>
          <p>
            <span>&#x25cf;</span> <b>{order.status}</b>
          </p>

          <button className="btn">Track Order</button>
        </div>
      ))}
    </div>
  </div>
);

  
}

export default MyOrders
