import React, { useContext, useState } from 'react';
import { StoreContext } from '../../context/StoreContext';
import './PlaceOrder.css';
import axios from 'axios';

const PlaceOrder = () => {
  const { getTotalCartAmount, token, food_list = [], cartItems = {}, url } = useContext(StoreContext);
  
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    street: "",
    city: "",
    state: "",
    zipcode: "",
    country: "",
    phone: ""
  });
  
  const [status, setStatus] = useState({
    loading: false,
    error: null,
    success: false
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const validateForm = () => {
    // Check if cart is empty
    if (getTotalCartAmount() === 0) {
      setStatus({
        loading: false,
        error: "Your cart is empty",
        success: false
      });
      return false;
    }

    // Basic email validation
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      setStatus({
        loading: false,
        error: "Please enter a valid email address",
        success: false
      });
      return false;
    }

    // Phone number validation
    if (!/^\d{10,15}$/.test(formData.phone)) {
      setStatus({
        loading: false,
        error: "Please enter a valid phone number (10-15 digits)",
        success: false
      });
      return false;
    }

    return true;
  };

  const handlePlaceOrder = async (e) => {
    e.preventDefault();
    setStatus({ loading: true, error: null, success: false });

    if (!validateForm()) return;

    try {
      // Prepare order items
      const orderItems = food_list.reduce((acc, item) => {
        if (cartItems[item._id] > 0) {
          acc.push({
            ...item,
            quantity: cartItems[item._id]
          });
        }
        return acc;
      }, []);

      const orderData = {
        address: formData, // Fixed the typo from 'adderess'
        items: orderItems,
        amount: getTotalCartAmount() + 2,
      };

      const response = await axios.post(
        `${url}/api/order/place`,
        orderData,
        { 
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        window.location.href = response.data.session_url;
      } else {
        throw new Error(response.data.message || "Order placement failed");
      }
    } catch (error) {
      console.error("Order Error:", error);
      setStatus({
        loading: false,
        error: error.response?.data?.message || 
              error.message || 
              "Failed to place order. Please try again.",
        success: false
      });
    }
  };

  // Calculate totals
  const subtotal = getTotalCartAmount();
  const deliveryFee = subtotal === 0 ? 0 : 2;
  const total = subtotal + deliveryFee;

  return (
    <form onSubmit={handlePlaceOrder} className="place-order">
      <div className="place-order-left">
        <p className="title">Delivery Information</p>
        
        <div className="multi-fields">
          <input
            name="firstName"
            value={formData.firstName}
            onChange={handleInputChange}
            type="text"
            placeholder="First name"
            required
            minLength="2"
          />
          <input
            name="lastName"
            value={formData.lastName}
            onChange={handleInputChange}
            type="text"
            placeholder="Last name"
            required
            minLength="2"
          />
        </div>

        <input
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          type="email"
          placeholder="Email address"
          required
        />

        <input
          name="street"
          value={formData.street}
          onChange={handleInputChange}
          type="text"
          placeholder="Street"
          required
          minLength="5"
        />

        <div className="multi-fields">
          <input
            name="city"
            value={formData.city}
            onChange={handleInputChange}
            type="text"
            placeholder="City"
            required
          />
          <input
            name="state"
            value={formData.state}
            onChange={handleInputChange}
            type="text"
            placeholder="State"
            required
          />
        </div>

        <div className="multi-fields">
          <input
            name="zipcode"
            value={formData.zipcode}
            onChange={handleInputChange}
            type="text"
            placeholder="Zip code"
            required
            pattern="\d{5,10}"
          />
          <input
            name="country"
            value={formData.country}
            onChange={handleInputChange}
            type="text"
            placeholder="Country"
            required
          />
        </div>

        <input
          name="phone"
          value={formData.phone}
          onChange={handleInputChange}
          type="tel"
          placeholder="Phone number"
          required
          pattern="\d{10,15}"
        />
      </div>

      <div className="place-order-right">
        <div className="cart-total">
          <h2>Cart Totals</h2>
          
          <div className="cart-total-details">
            <p>Subtotal</p>
            <p>${subtotal.toFixed(2)}</p>
          </div>
          
          <hr />
          
          <div className="cart-total-details">
            <p>Delivery Fee</p>
            <p>${deliveryFee.toFixed(2)}</p>
          </div>
          
          <hr />
          
          <div className="cart-total-details">
            <b>Total</b>
            <b>${total.toFixed(2)}</b>
          </div>

          {status.error && (
            <div className="error-message">
              {status.error}
            </div>
          )}

          <button 
            type="submit" 
            disabled={status.loading || subtotal === 0}
            className={status.loading ? 'loading' : ''}
          >
            {status.loading ? 'Processing...' : 'PROCEED TO PAYMENT'}
          </button>
        </div>
      </div>
    </form>
  );
};

export default PlaceOrder;
