import React, { useContext } from 'react';
import './food-display.css'
import { StoreContext } from '../../context/StoreContext';
import FoodItem from '../Food_Item/Food_Item'; // ✅ Correct if this file exists

const FoodDisplay = ({category}) => {
  const { food_list } = useContext(StoreContext);

  return (
    <div className='food-display' id="food-display">
      <h1>Top Dishes near You.</h1>
      <div className="food-display-list">
      {food_list
          .filter(item => item && (category === "All" || category === item.category))
          .map((item, index) => (
            <FoodItem 
              key={index}
              id={item && item._id ? item._id : ""}
              name={item && item.name ? item.name : ""}
              description={item && item.description ? item.description : ""}
              price={item && item.price ? item.price : 0}
              image={item && item.image ? item.image : ""}
            />
            //              ^^^^^^^^^^^^^^
            //        HIGHLIGHT: safest way to only use defined props!
          ))}
      </div>
    </div>
  );
};

export default FoodDisplay;
